/// in_favorites : false
/// in_cart : false

class Products {
  Products({
      num? id, 
      num? price, 
      num? oldPrice, 
      num? discount, 
      String? image, 
      String? name, 
      String? description, 
      List<String>? images, 
      bool? inFavorites, 
      bool? inCart,}){
    _id = id;
    _price = price;
    _oldPrice = oldPrice;
    _discount = discount;
    _image = image;
    _name = name;
    _description = description;
    _images = images;
    _inFavorites = inFavorites;
    _inCart = inCart;
}

  Products.fromJson(dynamic json) {
    _id = json['id'];
    _price = json['price'];
    _oldPrice = json['old_price'];
    _discount = json['discount'];
    _image = json['image'];
    _name = json['name'];
    _description = json['description'];
    _images = json['images'] != null ? json['images'].cast<String>() : [];
    _inFavorites = json['in_favorites'];
    _inCart = json['in_cart'];
  }
  num? _id;
  num? _price;
  num? _oldPrice;
  num? _discount;
  String? _image;
  String? _name;
  String? _description;
  List<String>? _images;
  bool? _inFavorites;
  bool? _inCart;

  Iterable<Products> get data => [];
Products copyWith({  num? id,
  num? price,
  num? oldPrice,
  num? discount,
  String? image,
  String? name,
  String? description,
  List<String>? images,
  bool? inFavorites,
  bool? inCart,
}) => Products(  id: id ?? _id,
  price: price ?? _price,
  oldPrice: oldPrice ?? _oldPrice,
  discount: discount ?? _discount,
  image: image ?? _image,
  name: name ?? _name,
  description: description ?? _description,
  images: images ?? _images,
  inFavorites: inFavorites ?? _inFavorites,
  inCart: inCart ?? _inCart,
);
  num? get id => _id;
  num? get price => _price;
  num? get oldPrice => _oldPrice;
  num? get discount => _discount;
  String? get image => _image;
  String? get name => _name;
  String? get description => _description;
  List<String>? get images => _images;
  bool? get inFavorites => _inFavorites;
  bool? get inCart => _inCart;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['id'] = _id;
    map['price'] = _price;
    map['old_price'] = _oldPrice;
    map['discount'] = _discount;
    map['image'] = _image;
    map['name'] = _name;
    map['description'] = _description;
    map['images'] = _images;
    map['in_favorites'] = _inFavorites;
    map['in_cart'] = _inCart;
    return map;
  }

}